const version=require("../package.json").version;const utils=require("./utils");const abi=require("./abi");const Contract=require("./contract");const HttpRequest=require("./httprequest");const Accounts=require("./accounts");class Mcp{constructor(t){if(t){this.dev=t.dev===true?true:false;this.host=t.host?t.host:"127.0.0.1";this.port=t.port?t.port:"8765"}else{this.dev=false}this.request=new HttpRequest({host:this.host,port:this.port});this.accounts=new Accounts(this.dev);this.version=version;this.utils=utils;this.abi=abi;this.Contract=Contract}}module.exports=Mcp;